import React, { useEffect, useState } from 'react';
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Tooltip,
  IconButton,
  Chip,
  Switch,
  TablePagination,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Snackbar,
  Alert
} from '@mui/material';
import { createTheme, ThemeProvider, styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import RefreshIcon from '@mui/icons-material/Refresh';
import DownloadIcon from '@mui/icons-material/Download';
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';

const DashboardContainer = styled("div")(({ theme }) => ({
  padding: 9,
  minHeight: "100vh",
  backgroundColor: theme.palette.background.default,
  color: theme.palette.text.primary,
  fontFamily: "Poppins, sans-serif",
}));

const TitleContainer = styled("div")({
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  marginBottom: 20,
  padding: "15px 0",
  backgroundColor: "#036649",
  borderRadius: "8px",
  color: "#ffffff",
  position: "relative",
});

const Logo = styled("div")({
  display: "flex",
  alignItems: "center",
  gap: "10px",
  fontWeight: "bold",
  fontSize: "1.2rem",
});

const LogoIcon = () => (
  <svg width="40" height="40" viewBox="0 0 24 24" fill="#ffffff" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="#ffffff" strokeWidth="2" fill="#ffffff" />
    <text x="12" y="16" textAnchor="middle" fill="#036649" fontSize="10px" fontWeight="bold">DD</text>
  </svg>
);

const ControlsContainer = styled("div")(({ theme }) => ({
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: 20,
  backgroundColor: theme.palette.mode === "dark" ? "#333" : "#f7f7f7",
  padding: "10px",
  borderRadius: "8px",
  boxShadow: theme.shadows[3],
}));

const Title = styled(Typography)(({ theme }) => ({
  color: "#ffffff",
  fontWeight: "bold",
  fontSize: "1.5rem",
  fontFamily: "Poppins, sans-serif",
}));

// Define the specific columns and their order for DynamoDB data
const dynamoDBColumns = [
  { id: 'vmname', label: 'VM Name' },
  { id: 'failedcounter', label: 'Failed Counter' },
  { id: 'ticket_created', label: 'Ticket Created' },
  { id: 'message', label: 'Error Message' }
];

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [filteredData, setFilteredData] = useState({});
  const [loading, setLoading] = useState(false);
  const [loadingPage, setLoadingPage] = useState(true);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const lightTheme = createTheme({
    palette: {
      mode: "light",
      primary: { main: "#036649" },
      secondary: { main: "#00a676" },
      background: { default: "#f3f3f3" },
    },
  });

  const darkTheme = createTheme({
    palette: {
      mode: "dark",
      primary: { main: "#036649" },
      secondary: { main: "#00a676" },
      background: { default: "#121212" },
      text: { primary: "#ffffff" },
    },
  });

  const processTablesData = (data) => {
    const groupedData = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = {
          data: [],
        };
      }
      // Ensure data is in the correct order for display
      const processedItem = {
        vmname: item.vmname || '',
        failedcounter: item.failedcounter || '',
        ticket_created: item.ticket_created || '',
        message: item.message || '',
        ...item // Keep other fields for compatibility
      };
      acc[tableName].data.push(processedItem);
      return acc;
    }, {});

    return groupedData;
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data");
      const data = await response.json();
      const processedData = processTablesData(data);
      setTablesData(processedData);
      setFilteredData(processedData);
      checkForCriticalData(processedData);
    } catch (error) {
      console.error("Error fetching data:", error);
      setAlertMessage("Failed to fetch data. Please try again.");
      setOpenSnackbar(true);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (!selectedTable) return;

    const tableData = tablesData[selectedTable]?.data || [];
    const filteredTableData = tableData.filter((item) =>
      Object.entries(item).some(([key, value]) => {
        if (!value) return false;
        return value.toString().toLowerCase().includes(searchText.toLowerCase());
      })
    );

    setFilteredData({
      ...filteredData,
      [selectedTable]: {
        ...tablesData[selectedTable],
        data: filteredTableData,
      },
    });
    setPage(0);
  };

  const checkForCriticalData = (data) => {
    let foundCriticalData = false;
    Object.values(data).forEach(table => {
      table.data.forEach(row => {
        if (parseInt(row.failedcounter) > 3) {
          foundCriticalData = true;
        }
      });
    });

    if (foundCriticalData) {
      setAlertMessage("Critical alert: High failure count detected!");
      setOpenSnackbar(true);
    }
  };

  const exportToCSV = async () => {
    if (!selectedTable || !tablesData[selectedTable]) return;
    try {
      const dataToExport = tablesData[selectedTable].data.map(row => {
        return {
          'VM Name': row.vmname,
          'Failed Counter': row.failedcounter,
          'Ticket Created': row.ticket_created,
          'Error Message': row.message
        };
      });

      const headers = dynamoDBColumns.map(col => col.label).join(',');
      const csvData = dataToExport.map(row => 
        dynamoDBColumns.map(col => `"${row[col.label]}"`).join(',')
      ).join('\n');

      const blob = new Blob([`${headers}\n${csvData}`], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${selectedTable}_${new Date().toISOString()}.csv`;
      link.click();
    } catch (error) {
      console.error("Error exporting CSV:", error);
      setAlertMessage("Failed to export data. Please try again.");
      setOpenSnackbar(true);
    }
  };

  useEffect(() => {
    setTimeout(() => {
      fetchData();
      setLoadingPage(false);
    }, 1500);
  }, []);

  useEffect(() => {
    if (selectedTable) {
      handleSearch();
    }
  }, [searchText, selectedTable]);

  const paginatedData = selectedTable
    ? (filteredData[selectedTable]?.data || []).slice(
        page * rowsPerPage,
        page * rowsPerPage + rowsPerPage
      )
    : [];

  if (loadingPage) {
    return (
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh",
          backgroundColor: "#036649",
        }}
      >
        <Typography variant="h4" style={{ color: "#ffffff", marginBottom: 20 }}>
          Welcome to Dave Dashboard
        </Typography>
        <CircularProgress style={{ color: "#ffffff" }} />
      </motion.div>
    );
  }

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <DashboardContainer>
        <TitleContainer>
          <Logo>
            <LogoIcon />
            <Title variant="h5">Dave - Dashboard</Title>
          </Logo>
          <div style={{ position: "absolute", right: 20 }}>
            <Switch
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
              color="default"
            />
          </div>
        </TitleContainer>

        <ControlsContainer>
          <Select
            value={selectedTable}
            onChange={(e) => setSelectedTable(e.target.value)}
            displayEmpty
            style={{ minWidth: 200 }}
          >
            <MenuItem value="" disabled>Select Table</MenuItem>
            {Object.keys(tablesData).map((tableName) => (
              <MenuItem key={tableName} value={tableName}>
                {tableName}
              </MenuItem>
            ))}
          </Select>

          <TextField
            placeholder="Search..."
            onChange={(e) => setSearchText(e.target.value)}
            value={searchText}
            InputProps={{
              startAdornment: <SearchIcon style={{ marginRight: 8 }} />
            }}
          />

          <div>
            <Tooltip title="Refresh Data">
              <IconButton onClick={fetchData}>
                <RefreshIcon />
              </IconButton>
            </Tooltip>
            <Tooltip title="Export to CSV">
              <IconButton onClick={exportToCSV}>
                <DownloadIcon />
              </IconButton>
            </Tooltip>
          </div>
        </ControlsContainer>

        {loading ? (
          <CircularProgress style={{ display: "block", margin: "20px auto" }} />
        ) : (
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow style={{ backgroundColor: "#036649" }}>
                  {dynamoDBColumns.map((column) => (
                    <TableCell
                      key={column.id}
                      style={{ color: "#ffffff", fontWeight: "bold" }}
                    >
                      {column.label}
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {paginatedData.map((row, index) => (
                  <TableRow
                    key={index}
                    onClick={() => {
                      setSelectedRow(row);
                      setOpenDialog(true);
                    }}
                    hover
                    style={{ cursor: "pointer" }}
                  >
                    {dynamoDBColumns.map((column) => (
                      <TableCell key={column.id}>
                        {row[column.id]}
                      </TableCell>
                    ))}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={(filteredData[selectedTable]?.data || []).length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={(_, newPage) => setPage(newPage)}
              onRowsPerPageChange={(event) => {
                setRowsPerPage(parseInt(event.target.value, 10));
                setPage(0);
              }}
            />
          </TableContainer>
        )}

        <Dialog
          open={openDialog}
          onClose={() => setOpenDialog(false)}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle>
            Row Details
            <IconButton
              onClick={() => setOpenDialog(false)}
              style={{ position: "absolute", right: 8, top: 8 }}
            >
              <CloseIcon />
            </IconButton>
          </DialogTitle>
          <DialogContent dividers>
            {selectedRow && dynamoDBColumns.map((column) => (
              <Typography key={column.id} paragraph>
                <strong>{column.label}:</strong> {selectedRow[column.id]}
              </Typography>
            ))}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenDialog(false)} color="primary">
              Close
            </Button>
          </DialogActions>
        </Dialog>

        <Snackbar
          open={openSnackbar}
          autoHideDuration={6000}
          onClose={() => setOpenSnackbar(false)}
          anchorOrigin={{ vertical: "top", horizontal: "center" }}
        >
          <Alert 
            onClose={() => setOpenSnackbar(false)} 
            severity="error"
            sx={{ width: '100%' }}
          >
            {alertMessage}
          </Alert>
        </Snackbar>
      </DashboardContainer>
    </ThemeProvider>
  );
};

export default App;
