import { useEffect, useState } from "react";
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Tooltip,
  Snackbar,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  Button,
  Alert,
  Chip,
  LinearProgress,
  Switch
} from "@mui/material";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import CloseIcon from "@mui/icons-material/Close";
import RefreshIcon from "@mui/icons-material/Refresh";
import '../src/App.css';

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [openDetails, setOpenDetails] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [darkMode, setDarkMode] = useState(false); // Dark mode state

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    setLoading(true);
    fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data")
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        setOpenSnackbar(true);
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
      })
      .catch((err) => {
        setLoading(false);
        console.error("Couldn't fetch data", err);
      });
  };

  const groupAndGenerateColumns = (data) => {
    const groups = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = { data: [], keys: new Set() };
      }
      acc[tableName].data.push(item);
      Object.keys(item).forEach((key) => {
        if (key !== "table_name") acc[tableName].keys.add(key);
      });
      return acc;
    }, {});

    Object.keys(groups).forEach((tableName) => {
      groups[tableName].columns = Array.from(groups[tableName].keys).map(
        (key) => ({
          title: key.replace(/_/g, " ").replace(/\b\w/g, (l) => l.toUpperCase()),
          dataIndex: key,
          key: key,
        })
      );
    });

    return groups;
  };

  const handleTableChange = (event) => {
    setSelectedTable(event.target.value);
    setSearchText("");
    setStatusFilter("");
  };

  const handleSearch = (event) => {
    setSearchText(event.target.value.toLowerCase());
  };

  const handleStatusFilterChange = (event) => {
    setStatusFilter(event.target.value);
  };

  // Combine search and filter conditions
  const filteredData =
    selectedTable && tablesData[selectedTable]
      ? tablesData[selectedTable].data.filter((item) =>
          // Apply search filter
          Object.values(item).some(
            (val) =>
              val &&
              val.toString().toLowerCase().includes(searchText)
          ) &&
          // Apply backup_status filter
          (statusFilter === "" || item.backup_status === statusFilter)
        )
      : [];

  const handleRowClick = (row) => {
    setSelectedRow(row);
    setOpenDetails(true);
  };

  const handleCloseSnackbar = () => setOpenSnackbar(false);
  const handleCloseDetails = () => setOpenDetails(false);

  // Create themes for light and dark modes
  const lightTheme = createTheme({
    palette: {
      mode: "light",
      primary: { main: "#036649" },
      background: { default: "#ffffff" },
    },
  });

  const darkTheme = createTheme({
    palette: {
      mode: "dark",
      primary: { main: "#bb86fc" },
      background: { default: "#121212" },
      text: { primary: "#ffffff" },
    },
  });

  // Toggle switch handler for dark mode
  const handleDarkModeToggle = () => {
    setDarkMode(!darkMode);
  };

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <div style={{ padding: 20, minHeight: "100vh", backgroundColor: darkMode ? "#121212" : "#ffffff" }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <Typography variant="h4" style={{ color: darkMode ? "#ffffff" : "#036649" }}>
            Dave - Dashboard
          </Typography>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Typography variant="body1" style={{ color: darkMode ? "#ffffff" : "#036649", marginRight: 8 }}>
              Dark Mode
            </Typography>
            <Switch checked={darkMode} onChange={handleDarkModeToggle} />
          </div>
        </div>

        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 20,
            backgroundColor: darkMode ? "#424242" : "#f5f5f5",
            padding: "10px",
            borderRadius: "8px",
          }}
        >
          <Select
            value={selectedTable}
            onChange={handleTableChange}
            displayEmpty
            variant="outlined"
            style={{
              width: 200,
              backgroundColor: darkMode ? "#333333" : "#ffffff",
              color: darkMode ? "#ffffff" : "#036649",
            }}
          >
            <MenuItem value="" disabled>
              Select Module
            </MenuItem>
            {Object.keys(tablesData).map((tableName) => (
              <MenuItem key={tableName} value={tableName}>
                {tableName}
              </MenuItem>
            ))}
          </Select>

          <TextField
            placeholder="Search Table"
            variant="outlined"
            onChange={handleSearch}
            value={searchText}
            style={{
              width: 300,
              backgroundColor: darkMode ? "#333333" : "#ffffff",
              color: darkMode ? "#ffffff" : "#036649",
            }}
            InputProps={{
              style: { color: darkMode ? "#ffffff" : "#036649" },
            }}
          />

          {/* Dropdown filter for backup_status */}
          <Select
            value={statusFilter}
            onChange={handleStatusFilterChange}
            displayEmpty
            variant="outlined"
            style={{
              width: 150,
              backgroundColor: darkMode ? "#333333" : "#ffffff",
              color: darkMode ? "#ffffff" : "#036649",
            }}
          >
            <MenuItem value="">All Statuses</MenuItem>
            <MenuItem value="Success">Success</MenuItem>
            <MenuItem value="Fail">Fail</MenuItem>
          </Select>

          <Tooltip title="Refresh Data">
            <IconButton onClick={fetchData} style={{ color: darkMode ? "#bb86fc" : "#036649" }}>
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </div>

        {loading ? (
          <CircularProgress style={{ display: "block", margin: "20px auto", color: darkMode ? "#bb86fc" : "#036649" }} />
        ) : (
          selectedTable && tablesData[selectedTable] ? (
            <TableContainer component={Paper} style={{ borderRadius: "8px", backgroundColor: darkMode ? "#333333" : "#ffffff" }}>
              <Table>
                <TableHead>
                  <TableRow style={{ backgroundColor: darkMode ? "#333333" : "#036649" }}>
                    {tablesData[selectedTable].columns.map((col) => (
                      <TableCell key={col.key} style={{ color: darkMode ? "#ffffff" : "#ffffff", fontWeight: "bold" }}>
                        {col.title}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {filteredData.length > 0 ? (
                    filteredData.map((row, rowIndex) => (
                      <TableRow
                        key={rowIndex}
                        style={{
                          backgroundColor:
                            row.backup_status === "Fail" ? (darkMode ? "#432d2d" : "#fdecea") : (darkMode ? "#2d4d32" : "#e7f5e6"),
                          cursor: "pointer",
                        }}
                        onClick={() => handleRowClick(row)}
                      >
                        {tablesData[selectedTable].columns.map((col) => (
                          <TableCell key={col.key}>
                            {/* Display a Chip for backup_status */}
                            {col.key === "backup_status" ? (
                              <Chip
                                label={row[col.key]}
                                color={row[col.key] === "Fail" ? "error" : "success"}
                                style={{ fontWeight: "bold", color: "#ffffff" }}
                              />
                            ) : col.key === "completion_percentage" ? (
                              // Display a progress bar for completion_percentage
                              <div style={{ minWidth: "100px" }}>
                                <LinearProgress
                                  variant="determinate"
                                  value={row[col.key]}
                                  style={{ height: "10px", borderRadius: "5px" }}
                                />
                                <Typography variant="caption" display="block" align="center">
                                  {row[col.key]}%
                                </Typography>
                              </div>
                            ) : (
                              row[col.key]
                            )}
                          </TableCell>
                        ))}
                      </TableRow>
                    ))
                  ) : (
                    <TableRow>
                      <TableCell colSpan={tablesData[selectedTable].columns.length} align="center">
                        <Alert severity="info">No matching data found</Alert>
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          ) : (
            <Typography align="center" color="textSecondary">
              No Data
            </Typography>
          )
        )}

        <Snackbar
          open={openSnackbar}
          autoHideDuration={3000}
          onClose={handleCloseSnackbar}
          message="Data refreshed successfully"
          action={
            <IconButton size="small" color="inherit" onClick={handleCloseSnackbar}>
              <CloseIcon fontSize="small" />
            </IconButton>
          }
        />

        <Dialog open={openDetails} onClose={handleCloseDetails}>
          <DialogTitle>Row Details</DialogTitle>
          <DialogContent dividers>
            {selectedRow &&
              Object.entries(selectedRow).map(([key, value]) => (
                <Typography key={key}>
                  <strong>{key.replace(/_/g, " ")}:</strong> {value}
                </Typography>
              ))}
          </DialogContent>
          <Button onClick={handleCloseDetails} color="primary">
            Close
          </Button>
        </Dialog>
      </div>
    </ThemeProvider>
  );
};

export default App;
