import React, { useEffect, useState } from 'react';
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Tooltip,
  IconButton,
  Chip,
  Switch,
  TablePagination,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Snackbar,
  Alert
} from '@mui/material';
import { createTheme, ThemeProvider, styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import RefreshIcon from '@mui/icons-material/Refresh';
import DownloadIcon from '@mui/icons-material/Download';
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import { json2csv } from 'json-2-csv';
import '@fontsource/poppins';

// Styled Components
const DashboardContainer = styled("div")(({ theme }) => ({
  padding: 9,
  minHeight: "100vh",
  backgroundColor: theme.palette.background.default,
  color: theme.palette.text.primary,
  fontFamily: "Poppins, sans-serif",
}));

const TitleContainer = styled("div")({
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  marginBottom: 20,
  padding: "15px 0",
  backgroundColor: "#036649",
  borderRadius: "8px",
  color: "#ffffff",
  position: "relative",
});

const Logo = styled("div")({
  display: "flex",
  alignItems: "center",
  gap: "10px",
  fontWeight: "bold",
  fontSize: "1.2rem",
});

const LogoIcon = () => (
  <svg width="40" height="40" viewBox="0 0 24 24" fill="#ffffff" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="#ffffff" strokeWidth="2" fill="#ffffff" />
    <text x="12" y="16" textAnchor="middle" fill="#036649" fontSize="10px" fontWeight="bold">DD</text>
  </svg>
);

const FooterContainer = styled("footer")({
  backgroundColor: "#036649",
  color: "#ffffff",
  padding: "15px",
  textAlign: "center",
  marginTop: "20px",
  fontSize: "0.9rem",
});

const ControlsContainer = styled("div")(({ theme }) => ({
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: 20,
  backgroundColor: theme.palette.mode === "dark" ? "#333" : "#f7f7f7",
  padding: "10px",
  borderRadius: "8px",
  boxShadow: theme.shadows[3],
}));

const EnhancedButton = styled(IconButton)(({ theme }) => ({
  color: theme.palette.primary.main,
  "&:hover": {
    color: theme.palette.secondary.main,
    transform: "scale(1.1)",
  },
}));

const Title = styled(Typography)(({ theme }) => ({
  color: "#ffffff",
  fontWeight: "bold",
  fontSize: "1.5rem",
  fontFamily: "Poppins, sans-serif",
}));

// Helper Functions
const formatFieldName = (field) => {
  return field
    .replace(/_/g, ' ')
    .replace(/\b\w/g, l => l.toUpperCase())
    .trim();
};

const isHiddenField = (field) => {
  const hiddenFields = ['table_name']; // Only hide table_name
  return hiddenFields.includes(field.toLowerCase());
};

const detectColumnType = (key, value) => {
  const keyLower = key.toLowerCase();
  if (keyLower.includes('status')) return 'status';
  if (keyLower.includes('date') || keyLower.includes('time')) return 'date';
  if (keyLower.includes('id') && !keyLower.includes('guid')) return 'id';
  if (typeof value === 'object') return 'object';
  if (typeof value === 'boolean') return 'boolean';
  if (typeof value === 'number') return 'number';
  return 'text';
};

const detectTableStructure = (data) => {
  if (!data || data.length === 0) return { schema: {}, columnOrder: [] };

  const schema = {};
  // Get the first item to determine initial column order, including id fields
  const firstItem = data[0];
  // Keep all columns except table_name in the order they appear
  const columnOrder = Object.keys(firstItem).filter(key => !isHiddenField(key));

  // Build schema while maintaining order
  data.forEach(item => {
    Object.entries(item).forEach(([key, value]) => {
      if (!isHiddenField(key)) {
        if (!schema[key]) {
          schema[key] = detectColumnType(key, value);
        }
      }
    });
  });

  return { schema, columnOrder };
};

const processTablesData = (data) => {
  // Group data by table_name
  const groupedData = data.reduce((acc, item) => {
    const tableName = item.table_name;
    if (!acc[tableName]) {
      acc[tableName] = {
        data: [],
        schema: null,
        columnOrder: [],
      };
    }
    acc[tableName].data.push(item);
    return acc;
  }, {});

  // Process each table
  Object.keys(groupedData).forEach(tableName => {
    const { schema, columnOrder } = detectTableStructure(groupedData[tableName].data);
    groupedData[tableName].schema = schema;
    groupedData[tableName].columnOrder = columnOrder;
  });

  return groupedData;
};
