import { useEffect, useState } from "react";
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Tooltip,
  IconButton,
  Switch,
  TablePagination,
  Box,
} from "@mui/material";
import { createTheme, ThemeProvider, styled } from "@mui/material/styles";
import { motion } from "framer-motion";
import RefreshIcon from "@mui/icons-material/Refresh";
import DownloadIcon from "@mui/icons-material/Download";
import SearchIcon from "@mui/icons-material/Search";
import { json2csv } from "json-2-csv";
import "@fontsource/poppins";

const DashboardContainer = styled("div")(({ theme }) => ({
  padding: 20,
  minHeight: "100vh",
  backgroundColor: theme.palette.background.default,
  color: theme.palette.text.primary,
  fontFamily: "Poppins, sans-serif",
}));

const TitleContainer = styled("div")({
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  position: "relative",
  marginBottom: 20,
});

const FooterContainer = styled("footer")(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#222" : "#036649",
  color: theme.palette.mode === "dark" ? "#ccc" : "#ffffff",
  padding: "15px",
  textAlign: "center",
  marginTop: "20px",
  fontSize: "0.9rem",
}));

const ControlsContainer = styled("div")(({ theme }) => ({
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: 20,
  backgroundColor: theme.palette.mode === "dark" ? "#333" : "#f7f7f7",
  padding: "10px",
  borderRadius: "8px",
  boxShadow: theme.shadows[3],
}));

const TableWrapper = styled(TableContainer)(({ theme }) => ({
  maxWidth: "80%", // Limits the width of the table container to 80% of the viewport
  margin: "0 auto", // Centers the table container
  borderRadius: "8px",
  backgroundColor: theme.palette.mode === "dark" ? "#333333" : "#ffffff",
  boxShadow: theme.palette.mode === "dark" ? "0px 4px 8px rgba(0, 0, 0, 0.3)" : "0px 4px 8px rgba(0, 0, 0, 0.1)",
}));

const EnhancedButton = styled(IconButton)(({ theme }) => ({
  color: theme.palette.primary.main,
  "&:hover": {
    color: theme.palette.secondary.main,
    transform: "scale(1.1)",
  },
}));

const Logo = styled("div")({
  display: "flex",
  alignItems: "center",
  gap: "10px",
  fontWeight: "bold",
  fontSize: "1.2rem",
});

const LogoIcon = () => (
  <svg width="40" height="40" viewBox="0 0 24 24" fill="#036649" xmlns="http://www.w3.org/2000/svg">
    <circle cx="12" cy="12" r="10" stroke="#036649" strokeWidth="2" fill="#036649" />
    <text x="12" y="16" textAnchor="middle" fill="#ffffff" fontSize="10px" fontWeight="bold">DD</text>
  </svg>
);

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [loadingPage, setLoadingPage] = useState(true);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    setTimeout(() => {
      fetchData();
      setLoadingPage(false);
    }, 1500);
  }, []);

  const fetchData = () => {
    setLoading(true);
    fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data")
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
      })
      .catch((err) => {
        setLoading(false);
        console.error("Couldn't fetch data", err);
      });
  };

  const groupAndGenerateColumns = (data) => {
    const groups = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = { data: [], keys: new Set() };
      }
      acc[tableName].data.push(item);
      Object.keys(item).forEach((key) => {
        if (key !== "table_name") acc[tableName].keys.add(key);
      });
      return acc;
    }, {});

    Object.keys(groups).forEach((tableName) => {
      groups[tableName].columns = Array.from(groups[tableName].keys).map(
        (key) => ({
          title: key.replace(/_/g, " ").replace(/\b\w/g, (l) => l.toUpperCase()),
          dataIndex: key,
          key: key,
        })
      );
    });

    return groups;
  };

  const lightTheme = createTheme({
    palette: {
      mode: "light",
      primary: { main: "#036649" },
      secondary: { main: "#00a676" },
      background: { default: "#f3f3f3" },
      fontFamily: "Poppins, sans-serif",
    },
  });

  const darkTheme = createTheme({
    palette: {
      mode: "dark",
      primary: { main: "#036649" },
      secondary: { main: "#00a676" },
      background: { default: "#121212" },
      text: { primary: "#ffffff" },
      fontFamily: "Poppins, sans-serif",
    },
  });

  const handleDarkModeToggle = () => setDarkMode(!darkMode);

  const handleChangePage = (event, newPage) => setPage(newPage);
  const handleChangeRowsPerPage = (event) => setRowsPerPage(parseInt(event.target.value, 10));

  const exportToCSV = async () => {
    const csvData = await json2csv(tablesData[selectedTable]?.data || []);
    const blob = new Blob([csvData], { type: "text/csv" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = "filtered_data.csv";
    link.click();
  };

  return (
    <ThemeProvider theme={darkMode ? darkTheme : lightTheme}>
      <DashboardContainer>
        <TitleContainer>
          <Logo>
            <LogoIcon />
            <Typography variant="h5" style={{ color: "#036649" }}>
              Dave Dashboard
            </Typography>
          </Logo>
          <div style={{ position: "absolute", right: 0 }}>
            <Typography variant="body1" style={{ color: darkMode ? "#ffffff" : "#036649", marginRight: 8 }}>
              Dark Mode
            </Typography>
            <Switch
              checked={darkMode}
              onChange={handleDarkModeToggle}
              color="default"
              sx={{
                "& .MuiSwitch-switchBase.Mui-checked": {
                  color: "#ffffff",
                },
                "& .MuiSwitch-switchBase.Mui-checked + .MuiSwitch-track": {
                  backgroundColor: "#ffffff",
                },
              }}
            />
          </div>
        </TitleContainer>

        <ControlsContainer>
          <Tooltip title="Select Module">
            <Select
              value={selectedTable}
              onChange={(e) => setSelectedTable(e.target.value)}
              displayEmpty
              variant="outlined"
              style={{
                width: 200,
                backgroundColor: darkMode ? "#333333" : "#ffffff",
                color: darkMode ? "#ffffff" : "#036649",
              }}
            >
              <MenuItem value="" disabled>
                Select Module
              </MenuItem>
              {Object.keys(tablesData).map((tableName) => (
                <MenuItem key={tableName} value={tableName}>
                  {tableName}
                </MenuItem>
              ))}
            </Select>
          </Tooltip>

          <Tooltip title="Search">
            <TextField
              placeholder="Search Table"
              variant="outlined"
              onChange={(e) => setSearchText(e.target.value.toLowerCase())}
              value={searchText}
              style={{
                width: 300,
                backgroundColor: darkMode ? "#333333" : "#ffffff",
              }}
              InputProps={{
                startAdornment: (
                  <IconButton>
                    <SearchIcon style={{ color: darkMode ? "#ffffff" : "#036649" }} />
                  </IconButton>
                ),
                style: { color: darkMode ? "#ffffff" : "#036649" },
              }}
            />
          </Tooltip>

          <Tooltip title="Refresh Data">
            <EnhancedButton onClick={fetchData}>
              <RefreshIcon />
            </EnhancedButton>
          </Tooltip>

          <Tooltip title="Export to CSV">
            <EnhancedButton onClick={exportToCSV}>
              <DownloadIcon />
            </EnhancedButton>
          </Tooltip>
        </ControlsContainer>

        {loading ? (
          <CircularProgress style={{ display: "block", margin: "20px auto", color: darkMode ? "#bb86fc" : "#036649" }} />
        ) : (
          <TableWrapper component={Paper}>
            <Table size="small">
              <TableHead>
                <TableRow style={{ backgroundColor: darkMode ? "#333333" : "#036649" }}>
                  {tablesData[selectedTable]?.columns.map((col) => (
                    <TableCell key={col.key} style={{ color: "#ffffff", fontWeight: "bold", padding: "8px" }}>
                      {col.title}
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {(tablesData[selectedTable]?.data || []).map((row, rowIndex) => (
                  <TableRow
                    key={rowIndex}
                    component={motion.tr}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                    style={{
                      backgroundColor: darkMode ? "#333333" : "#ffffff",
                      cursor: "pointer",
                      transition: "background-color 0.3s ease",
                    }}
                    onMouseEnter={(e) =>
                      (e.currentTarget.style.backgroundColor = darkMode ? "#444444" : "#f1f1f1")
                    }
                    onMouseLeave={(e) =>
                      (e.currentTarget.style.backgroundColor = darkMode ? "#333333" : "#ffffff")
                    }
                  >
                    {tablesData[selectedTable]?.columns.map((col) => (
                      <TableCell key={col.key} style={{ padding: "8px" }}>
                        {col.key === "backup_status" ? (
                          <span
                            style={{
                              color: row[col.key] === "Fail" ? "red" : "green",
                              fontWeight: "bold",
                            }}
                          >
                            {row[col.key]}
                          </span>
                        ) : (
                          row[col.key]
                        )}
                      </TableCell>
                    ))}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
            <TablePagination
              rowsPerPageOptions={[5, 10, 25]}
              component="div"
              count={(tablesData[selectedTable]?.data || []).length}
              rowsPerPage={rowsPerPage}
              page={page}
              onPageChange={handleChangePage}
              onRowsPerPageChange={handleChangeRowsPerPage}
              style={{ color: darkMode ? "#ffffff" : "#036649" }}
            />
          </TableWrapper>
        )}

        <FooterContainer>
          <Typography variant="body2">
            © 2024 Dave Dashboard | Streamlined Data Insights for Your Business
          </Typography>
          <Typography variant="body2" style={{ fontSize: "0.8rem" }}>
            Built with ♥ by the Dave Dashboard Team
          </Typography>
        </FooterContainer>
      </DashboardContainer>
    </ThemeProvider>
  );
};

export default App;
