import React, { useEffect, useState } from 'react';
import {
  Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Checkbox, TextField, TableSortLabel, Button
} from '@mui/material';

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [selected, setSelected] = useState([]);
  const [filter, setFilter] = useState("");
  const [order, setOrder] = useState('asc');
  const [orderBy, setOrderBy] = useState('');

  useEffect(() => {
    setLoading(true);
    fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data")
      .then(res => res.json())
      .then(data => {
        setLoading(false);
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
      })
      .catch(err => {
        setLoading(false);
        console.error("Couldn't fetch data", err);
      });
  }, []);

  const groupAndGenerateColumns = (data) => {
    return data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = { data: [], columns: new Set() };
      }
      acc[tableName].data.push(item);
      Object.keys(item).forEach(key => acc[tableName].columns.add(key));
      return acc;
    }, {});
  };

  const handleSelectAllClick = (event, tableName) => {
    if (event.target.checked) {
      const newSelecteds = tablesData[tableName].data.map(n => n.id);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleClick = (event, id) => {
    const selectedIndex = selected.indexOf(id);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, id);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1),
      );
    }
    setSelected(newSelected);
  };

  const isSelected = (id) => selected.indexOf(id) !== -1;

  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const filteredData = tablesData && Object.keys(tablesData).reduce((acc, tableName) => {
    acc[tableName] = {
      ...tablesData[tableName],
      data: tablesData[tableName].data.filter(item => item.vmname.toLowerCase().includes(filter.toLowerCase()))
    };
    return acc;
  }, {});

  return (
    <div>
      <Typography variant="h4" style={{ textAlign: 'center', margin: '20px 0' }}>Dave - Dashboard</Typography>
      <TextField
        value={filter}
        onChange={e => setFilter(e.target.value)}
        variant="outlined"
        placeholder="Filter by VM name"
        style={{ marginBottom: 20 }}
      />
      {loading ? <p>Loading...</p> : (
        Object.keys(filteredData).map(tableName => (
          <TableContainer component={Paper} key={tableName} style={{ marginBottom: 20 }}>
            <Typography variant="h6">{tableName}</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell padding="checkbox">
                    <Checkbox
                      indeterminate={selected.length > 0 && selected.length < filteredData[tableName].data.length}
                      checked={filteredData[tableName].data.length > 0 && selected.length === filteredData[tableName].data.length}
                      onChange={(event) => handleSelectAllClick(event, tableName)}
                      inputProps={{ 'aria-label': 'select all desserts' }}
                    />
                  </TableCell>
                  {Array.from(filteredData[tableName].columns).map(column => (
                    <TableCell key={column} align="center">
                      {column}
                      <TableSortLabel
                        active={orderBy === column}
                        direction={orderBy === column ? order : 'asc'}
                        onClick={(event) => handleRequestSort(event, column)}
                      />
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredData[tableName].data.map((row) => {
                  const isItemSelected = isSelected(row.id);
                  return (
                    <TableRow
                      hover
                      onClick={(event) => handleClick(event, row.id)}
                      role="checkbox"
                      aria-checked={isItemSelected}
                      tabIndex={-1}
                      key={row.id}
                      selected={isItemSelected}
                    >
                      <TableCell padding="checkbox">
                        <Checkbox checked={isItemSelected} />
                      </TableCell>
                      {Array.from(filteredData[tableName].columns).map(column => (
                        <TableCell key={`${row.id}-${column}`} align="center">{row[column]}</TableCell>
                      ))}
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </TableContainer>
        ))
      )}
    </div>
  );
};

export default App;
