import React, { useEffect, useState } from "react";
import { 
  Card,
  CardContent,
  CardHeader,
  CardTitle 
} from "@/components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import {
  Dialog,
  DialogContent,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { 
  RefreshCw, 
  Download, 
  Search, 
  CircleIcon
} from "lucide-react";

const DashboardContainer = () => {
  const [tablesData, setTablesData] = useState({});
  const [filteredData, setFilteredData] = useState({});
  const [loading, setLoading] = useState(false);
  const [loadingPage, setLoadingPage] = useState(true);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  useEffect(() => {
    setTimeout(() => {
      fetchData();
      setLoadingPage(false);
    }, 1500);
  }, []);

  useEffect(() => {
    if (selectedTable) {
      handleSearch();
    }
  }, [searchText, selectedTable, tablesData]);

  const fetchData = () => {
    setLoading(true);
    fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data")
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
        setFilteredData(groupedData);
        checkForCriticalData(groupedData);
      })
      .catch((err) => {
        setLoading(false);
        console.error("Couldn't fetch data", err);
        setAlertMessage("Failed to fetch data. Please try again.");
        setShowAlert(true);
      });
  };

  const groupAndGenerateColumns = (data) => {
    const groups = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = { data: [], keys: new Set() };
      }
      acc[tableName].data.push(item);
      Object.keys(item).forEach((key) => {
        if (key !== "table_name") acc[tableName].keys.add(key);
      });
      return acc;
    }, {});

    Object.keys(groups).forEach((tableName) => {
      groups[tableName].columns = Array.from(groups[tableName].keys).map(
        (key) => ({
          title: key.replace(/_/g, " ").replace(/\b\w/g, (l) => l.toUpperCase()),
          dataIndex: key,
          key: key,
        })
      );
    });

    return groups;
  };

  const checkForCriticalData = (data) => {
    let foundCriticalData = false;

    Object.keys(data).forEach((tableName) => {
      data[tableName].data.forEach((row) => {
        if (row.backup_status === "Fail" || row.status?.toLowerCase() === "inactive") {
          foundCriticalData = true;
        }
      });
    });

    if (foundCriticalData) {
      setAlertMessage("Critical alert: 'Fail' statuses or 'Inactive' items found in the data!");
      setShowAlert(true);
    }
  };

  const handleSearch = () => {
    if (!selectedTable) return;

    const tableData = tablesData[selectedTable].data;
    const filteredTableData = tableData.filter((item) =>
      Object.values(item).some((value) =>
        value && value.toString().toLowerCase().includes(searchText.toLowerCase())
      )
    );

    setFilteredData({
      ...filteredData,
      [selectedTable]: { ...tablesData[selectedTable], data: filteredTableData },
    });
    setPage(0);
  };

  const handleExport = () => {
    if (!selectedTable || !filteredData[selectedTable]?.data) return;

    const columns = tablesData[selectedTable].columns.map(col => col.title);
    const rows = filteredData[selectedTable].data.map(row => 
      tablesData[selectedTable].columns.map(col => row[col.key])
    );

    const csvContent = [
      columns.join(','),
      ...rows.map(row => row.join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `${selectedTable}_export.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const paginatedData = selectedTable
    ? filteredData[selectedTable]?.data.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
    : [];

  const getStatusBadgeVariant = (status) => {
    switch (status?.toLowerCase()) {
      case 'active':
        return 'success';
      case 'inactive':
        return 'destructive';
      case 'pending':
        return 'warning';
      default:
        return 'secondary';
    }
  };

  if (loadingPage) {
    return (
      <div className="flex items-center justify-center h-screen bg-green-800">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-white mb-4">Welcome to Dave Dashboard</h1>
          <CircleIcon className="animate-spin text-white h-8 w-8 mx-auto" />
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen p-4 ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
      {/* Header */}
      <Card className="bg-green-800 mb-6">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-xl font-bold text-white">Dave Dashboard</CardTitle>
          <div className="flex items-center space-x-2">
            <span className="text-sm text-white">Dark Mode</span>
            <Switch
              checked={darkMode}
              onCheckedChange={() => setDarkMode(!darkMode)}
            />
          </div>
        </CardHeader>
      </Card>

      {/* Controls */}
      <div className="flex flex-wrap gap-4 mb-6">
        <Select
          value={selectedTable}
          onValueChange={setSelectedTable}
        >
          <SelectTrigger className="w-[200px]">
            <SelectValue placeholder="Select Module" />
          </SelectTrigger>
          <SelectContent>
            {Object.keys(tablesData).map((tableName) => (
              <SelectItem key={tableName} value={tableName}>
                {tableName}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <div className="flex-1 max-w-sm relative">
          <Input
            placeholder="Search Table"
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            className="pl-10"
          />
          <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-500" />
        </div>

        <Button
          variant="outline"
          size="icon"
          onClick={fetchData}
          className="h-10 w-10"
        >
          <RefreshCw className="h-4 w-4" />
        </Button>

        <Button
          variant="outline"
          size="icon"
          onClick={handleExport}
          className="h-10 w-10"
        >
          <Download className="h-4 w-4" />
        </Button>
      </div>

      {/* Alert */}
      {showAlert && (
        <Alert variant="destructive" className="mb-6">
          <AlertTitle>Critical Alert</AlertTitle>
          <AlertDescription>{alertMessage}</AlertDescription>
        </Alert>
      )}

      {/* Table */}
      <div className="rounded-lg border bg-white dark:bg-gray-800 overflow-hidden">
        {loading ? (
          <div className="flex justify-center p-8">
            <CircleIcon className="animate-spin h-8 w-8" />
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full border-collapse">
              <thead>
                <tr className="bg-green-800">
                  {tablesData[selectedTable]?.columns.map((col) => (
                    <th
                      key={col.key}
                      className="px-4 py-3 text-left text-white font-semibold text-sm"
                    >
                      {col.title}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200 dark:divide-gray-700">
                {paginatedData.map((row, rowIndex) => (
                  <tr
                    key={rowIndex}
                    onClick={() => {
                      setSelectedRow(row);
                      setOpenDialog(true);
                    }}
                    className="hover:bg-gray-50 dark:hover:bg-gray-700 cursor-pointer transition-colors"
                  >
                    {tablesData[selectedTable]?.columns.map((col) => (
                      <td
                        key={col.key}
                        className="px-4 py-3 text-sm"
                      >
                        {col.key === "backup_status" ? (
                          <Badge variant={row[col.key] === "Fail" ? "destructive" : "success"}>
                            {row[col.key]}
                          </Badge>
                        ) : col.key === "status" ? (
                          <Badge variant={getStatusBadgeVariant(row[col.key])}>
                            {row[col.key] || "N/A"}
                          </Badge>
                        ) : (
                          row[col.key]
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>

            {/* Pagination */}
            <div className="flex items-center justify-between px-4 py-3 border-t dark:border-gray-700">
              <div className="flex items-center gap-2">
                <span className="text-sm text-gray-700 dark:text-gray-300">
                  Rows per page:
                </span>
                <select
                  value={rowsPerPage}
                  onChange={(e) => {
                    setRowsPerPage(Number(e.target.value));
                    setPage(0);
                  }}
                  className="border rounded px-2 py-1 text-sm dark:bg-gray-700 dark:border-gray-600"
                >
                  {[5, 10, 25].map((pageSize) => (
                    <option key={pageSize} value={pageSize}>
                      {pageSize}
                    </option>
                  ))}
                </select>
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setPage(Math.max(0, page - 1))}
                  disabled={page === 0}
                >
                  Previous
                </Button>
                <span className="text-sm text-gray-700 dark:text-gray-300">
                  Page {page + 1} of{' '}
                  {Math.ceil((filteredData[selectedTable]?.data.length || 0) / rowsPerPage)}
                </span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() =>
                    setPage(Math.min(
                      Math.ceil((filteredData[selectedTable]?.data.length || 0) / rowsPerPage) - 1,
                      page + 1
                    ))
                  }
                  disabled={
                    page >=
                    Math.ceil((filteredData[selectedTable]?.data.length || 0) / rowsPerPage) - 1
                  }
                >
                  Next
                </Button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Row Details Dialog */}
      <Dialog open={openDialog} onOpenChange={setOpenDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Row Details</DialogTitle>
          </DialogHeader>
          {selectedRow && (
            <div className="space-y-4">
              {Object.entries(selectedRow).map(([key, value]) => (
                <div key={key} className="text-sm">
                  <strong>{key.replace(/_/g, " ")}:</strong> {value}
                </div>
              ))}
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setOpenDialog(false)}>
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Footer */}
      <div className="mt-6 p-4 bg-green-800 text-white text-center rounded-lg">
        <p className="text-sm">© 2024 Dave Dashboard | Streamlined Data Insights</p>
        <p className="text-xs mt-1">Built by IT Team</p>
      </div>
    </div>
  );
};

export default DashboardContainer;
