import { useEffect, useState } from "react";
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  IconButton,
  Switch,
  Chip,
  TablePagination,
} from "@mui/material";
import RefreshIcon from "@mui/icons-material/Refresh";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [statusFilter, setStatusFilter] = useState("");

  const theme = createTheme({
    palette: {
      mode: darkMode ? "dark" : "light",
    },
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    setLoading(true);
    // Fetch logic here
    setLoading(false);
  };

  const filteredData = selectedTable && tablesData[selectedTable]
    ? tablesData[selectedTable].data.filter(item =>
        (!statusFilter || item.backup_status === statusFilter) &&
        Object.values(item).some(val =>
          val && val.toString().toLowerCase().includes(searchText)
        )
      )
    : [];

  return (
    <ThemeProvider theme={theme}>
      <div style={{ padding: 20, minHeight: "100vh" }}>
        <Typography variant="h4" align="center">Dave - Dashboard</Typography>

        <Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />

        <Select value={statusFilter} onChange={e => setStatusFilter(e.target.value)}>
          <MenuItem value="">All</MenuItem>
          <MenuItem value="Fail"><Chip label="Fail" color="error" /></MenuItem>
          <MenuItem value="Success"><Chip label="Success" color="success" /></MenuItem>
        </Select>

        <TableContainer component={Paper}>
          <Table>
            <TableBody>
              {filteredData.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => (
                <TableRow>
                  {/* Map columns */}
                </TableRow>
              ))}
            </TableBody>
          </Table>
          <TablePagination
            rowsPerPageOptions={[10, 25, 50]}
            count={filteredData.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={(e, newPage) => setPage(newPage)}
            onRowsPerPageChange={e => setRowsPerPage(parseInt(e.target.value, 10))}
          />
        </TableContainer>
      </div>
    </ThemeProvider>
  );
};

export default App;
