import { useEffect, useState } from "react";
import {
  Typography,
  Select,
  MenuItem,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  IconButton,
  TableSortLabel
} from "@mui/material";
import RefreshIcon from "@mui/icons-material/Refresh";
import '../src/App.css';

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedTable, setSelectedTable] = useState("");
  const [searchText, setSearchText] = useState("");
  const [sortConfig, setSortConfig] = useState({ key: "", direction: "asc" });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    setLoading(true);
    // Fetch logic here
    setLoading(false);
  };

  const handleTableChange = (event) => {
    setSelectedTable(event.target.value);
    setSearchText("");
  };

  const handleSearch = (event) => {
    setSearchText(event.target.value.toLowerCase());
  };

  // Function to handle sorting
  const handleSortRequest = (columnKey) => {
    const isAsc = sortConfig.key === columnKey && sortConfig.direction === "asc";
    setSortConfig({ key: columnKey, direction: isAsc ? "desc" : "asc" });
  };

  // Apply search and sorting
  const sortedData = () => {
    let sortableItems = selectedTable && tablesData[selectedTable] ? [...tablesData[selectedTable].data] : [];

    // Apply search filter
    sortableItems = sortableItems.filter((item) =>
      Object.values(item).some(
        (val) =>
          val &&
          val.toString().toLowerCase().includes(searchText)
      )
    );

    // Apply sorting
    if (sortConfig.key) {
      sortableItems.sort((a, b) => {
        const aValue = a[sortConfig.key];
        const bValue = b[sortConfig.key];

        if (aValue < bValue) return sortConfig.direction === "asc" ? -1 : 1;
        if (aValue > bValue) return sortConfig.direction === "asc" ? 1 : -1;
        return 0;
      });
    }

    return sortableItems;
  };

  return (
    <div style={{ padding: 20, backgroundColor: "#ffffff", minHeight: "100vh" }}>
      <Typography variant="h4" align="center">Dave - Dashboard</Typography>

      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 20 }}>
        <Select
          value={selectedTable}
          onChange={handleTableChange}
          displayEmpty
          variant="outlined"
          style={{ width: 200, backgroundColor: "#ffffff" }}
        >
          <MenuItem value="" disabled>Select Module</MenuItem>
          {Object.keys(tablesData).map((tableName) => (
            <MenuItem key={tableName} value={tableName}>{tableName}</MenuItem>
          ))}
        </Select>

        <TextField
          placeholder="Search Table"
          variant="outlined"
          onChange={handleSearch}
          value={searchText}
          style={{ width: 300, backgroundColor: "#ffffff" }}
        />

        <IconButton onClick={fetchData}>
          <RefreshIcon />
        </IconButton>
      </div>

      {loading ? (
        <CircularProgress style={{ display: "block", margin: "20px auto" }} />
      ) : (
        selectedTable && tablesData[selectedTable] ? (
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  {tablesData[selectedTable].columns.map((col) => (
                    <TableCell key={col.key}>
                      <TableSortLabel
                        active={sortConfig.key === col.key}
                        direction={sortConfig.direction}
                        onClick={() => handleSortRequest(col.key)}
                      >
                        {col.title}
                      </TableSortLabel>
                    </TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {sortedData().map((row, rowIndex) => (
                  <TableRow key={rowIndex}>
                    {tablesData[selectedTable].columns.map((col) => (
                      <TableCell key={col.key}>{row[col.key]}</TableCell>
                    ))}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <Typography align="center" color="textSecondary">No Data</Typography>
        )
      )}
    </div>
  );
};

export default App;
