import { message, Skeleton, Table } from "antd";
import { useEffect, useState } from "react";

const App = () => {
  const [tablesData, setTablesData] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch("https://ayr7fudi20.execute-api.us-east-1.amazonaws.com/Test-stage/data")
      .then(res => res.json())
      .then(data => {
        setLoading(false);
        // Group data by table_name and generate columns for each table
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
      })
      .catch(err => {
        setLoading(false);
        message.error("Couldn't fetch data");
      });
  }, []);

  // Group data by 'table_name' and generate columns
  const groupAndGenerateColumns = (data) => {
    const groups = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = {
          data: [],
          keys: new Set() // Store unique keys for each table
        };
      }
      acc[tableName].data.push(item);
      // Accumulate all keys except 'table_name'
      Object.keys(item).forEach(key => {
        if (key !== 'table_name') acc[tableName].keys.add(key);
      });
      return acc;
    }, {});

    // Generate columns for each group
    Object.keys(groups).forEach(tableName => {
      groups[tableName].columns = Array.from(groups[tableName].keys).map(key => ({
        title: key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()), // Format and capitalize titles
        dataIndex: key,
        key: key,
      }));
    });

    return groups;
  };

  return (
    <div>
      <div style={{ width: '100%', height: 50, backgroundColor: '#004D40', color: '#fff', display: 'flex', alignItems: 'center', padding: 10, fontSize: 20, fontWeight: '600', justifyContent: 'center' }}>
        Dave - Dashboard
      </div>
      {loading ? <Skeleton /> : (
        Object.entries(tablesData).map(([tableName, {data, columns}], index) => (
          <div key={index}>
            <h2>{tableName}</h2>
            <Table dataSource={data} columns={columns} />
          </div>
        ))
      )}
    </div>
  );
}

export default App;
