import { message, Skeleton, Table, Select, Typography, Button } from "antd";
import { useEffect, useState } from "react";
import { SearchOutlined } from '@ant-design/icons';
import '../src/App.css'; // Ensure this path is correct

const { Option } = Select;

const App = () => {
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(false);
  const [selectedTable, setSelectedTable] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch("https://o2merk3yse.execute-api.us-east-1.amazonaws.com/dev/data")
      .then(res => res.json())
      .then(data => {
        setLoading(false);
        const groupedData = groupAndGenerateColumns(data);
        setTablesData(groupedData);
      })
      .catch(err => {
        setLoading(false);
        message.error("Couldn't fetch data");
      });
  }, []);

  const groupAndGenerateColumns = (data) => {
    const groups = data.reduce((acc, item) => {
      const tableName = item.table_name;
      if (!acc[tableName]) {
        acc[tableName] = { data: [], keys: new Set(), uniqueValues: {} };
      }
      acc[tableName].data.push(item);
      Object.keys(item).forEach(key => {
        if (key !== 'table_name') {
          acc[tableName].keys.add(key);
          acc[tableName].uniqueValues[key] = acc[tableName].uniqueValues[key] || new Set();
          acc[tableName].uniqueValues[key].add(item[key]);
        }
      });
      return acc;
    }, {});

    Object.keys(groups).forEach(tableName => {
      groups[tableName].columns = Array.from(groups[tableName].keys).map(key => ({
        title: key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase()),
        dataIndex: key,
        key: key,
        ...getColumnSearchProps(key, Array.from(groups[tableName].uniqueValues[key]))
      }));
    });

    return groups;
  };

  const getColumnSearchProps = (dataIndex, filters) => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => {
      const onSelect = (value) => {
        const allValues = filters.slice();
        const isSelectAll = value.includes('Select All');
        if (isSelectAll) {
          setSelectedKeys(selectedKeys.length === allValues.length ? [] : allValues);
        } else {
          setSelectedKeys(value);
        }
      };

      return (
        <div style={{ padding: 8 }}>
          <Select
            mode="multiple"
            placeholder={`Filter ${dataIndex}`}
            value={selectedKeys}
            onChange={onSelect}
            style={{ width: 240, marginBottom: 8, display: 'block' }}
            dropdownRender={menu => (
              <>
                {menu}
                <div style={{ display: 'flex', flexWrap: 'nowrap', padding: 8 }}>
                  <Button
                    style={{ flex: 'auto', marginRight: 8 }}
                    onClick={() => onSelect(filters.slice())} // Select all
                  >
                    Select All
                  </Button>
                  <Button
                    style={{ flex: 'auto' }}
                    onClick={() => onSelect([])} // Clear all
                  >
                    Clear
                  </Button>
                </div>
              </>
            )}
            allowClear
          >
            {filters.map(filter => (
              <Option key={filter} value={filter}>{filter}</Option>
            ))}
            <Option key="Select All" value="Select All">Select All</Option>
          </Select>
          <Button
            type="primary"
            onClick={() => confirm()}
            size="small"
            style={{ width: '100%' }}
          >
            Filter
          </Button>
        </div>
      );
    },
    filterIcon: filtered => <SearchOutlined style={{ color: filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) => selectedKeys.length === 0 || selectedKeys.includes(record[dataIndex])
  });

  const handleTableChange = value => {
    setSelectedTable(value);
  };

  return (
    <div>
      <Typography.Title level={3} style={{ textAlign: 'center', backgroundColor: '#004D40', color: '#fff', height: 50, display: 'flex', alignItems: 'center', padding: 10, fontSize: 20, fontWeight: '600', justifyContent: 'center' }}>Dave - Dashboard</Typography.Title>
      <Select
        value={selectedTable}
        style={{ width: 200, marginBottom: 20 }}
        onChange={handleTableChange}
        placeholder="Select Module"
        className="custom-select"
      >
        {Object.keys(tablesData).map(tableName => (
          <Option key={tableName} value={tableName}>{tableName}</Option>
        ))}
      </Select>

      {loading ? <Skeleton /> : (
        selectedTable && tablesData[selectedTable] ? (
          <Table 
            dataSource={tablesData[selectedTable].data} 
            columns={tablesData[selectedTable].columns} 
            rowClassName={(record) => record.backup_status === "Fail" ? 'backup-fail' : 'backup-success'}
          />
        ) : <Typography.Text>No Data</Typography.Text>
      )}
    </div>
  );
}

export default App;
